Coveo.TemplateCache.registerTemplate("EmailThread", Coveo.HtmlTemplate.fromString("<div class=\"coveo-result-frame\">\n  <div class=\"coveo-result-row\">\n    <div class=\"coveo-result-cell coveo-no-wrap\" style=\"vertical-align: middle\">\n      <span class=\"CoveoIcon\" data-small=\"true\">\n      </span>\n      <a class=\"CoveoSalesforceResultLink\"></a>\n    </div>\n    <div class=\"coveo-result-cell\" style=\"text-align:right; padding-right:5px; width:124px;vertical-align: middle;font-size:12px;\">\n      <span class=\"CoveoAttachToCase\" data-display-text=\"false\"></span>\n      <span class=\"CoveoFieldValue\" data-field=\"@date\" data-helper=\"dateTime\"></span>\n    </div>\n    <div class=\"coveo-result-cell\" style=\"width:20px; vertical-align:middle\">\n      <div class=\"CoveoBoxResultAction\">\n        <div class=\"CoveoBoxQuickview\" data-template-id=\"EmailQuickviewContent\"></div>\n        <div class=\"CoveoBoxAttachToCase\"></div>\n      </div>\n    </div>\n  </div>\n  <div class=\"coveo-result-row\">\n    <div class=\"coveo-result-cell coveo-no-wrap\">\n      <span class=\"CoveoExcerpt\"></span>\n    </div>\n  </div>\n  <div class=\"coveo-result-row\">\n    <div class=\"coveo-result-cell coveo-no-wrap\">\n      <span class=\"CoveoResultAttachments\" data-result-template-id=\"EmailResultAttachment\">\n      </span>\n    </div>\n  </div>\n  <div class=\"coveo-result-row\">\n    <div class=\"coveo-result-cell\">\n      <span class=\"CoveoResultFolding\" data-result-template-id=\"EmailThread\" data-more-caption=\"ShowAllReplies\" data-less-caption=\"ShowOnlyMostRelevantReplies\">\n      </span>\n    </div>\n  </div>\n</div>", "raw.mailbox != undefined"), true, true)
Coveo.TemplateCache.registerTemplate("EmailResultAttachment", Coveo.UnderscoreTemplate.fromString("<div class=\"coveo-attachment-container\">\n    <% if(isMobileDevice() && (Coveo.QueryUtils.hasThumbnail(obj) || hasHtmlVersion)) { %>\n        <% if(hasHtmlVersion) { %>\n        <%= fromFileTypeToIcon() %>\n        <a class=\"CoveoSalesforceResultLink\" data-open-quickview=\"true\"><%= title || clickUri %></a>\n        <span class=\"CoveoQuickview\"></span>\n        <% } else if(Coveo.QueryUtils.hasThumbnail(obj)) { %>\n        <%= thumbnail() %>\n        <% } %>\n    <% } else { %>\n        <%= fromFileTypeToIcon() %>\n        <a class=\"CoveoSalesforceResultLink\"><%= title || clickUri %></a>\n        <span class=\"CoveoQuickview\"></span>\n    <% } %>\n</div>\n"), false, false)
Coveo.TemplateCache.registerTemplate("EmailQuickviewContent", Coveo.UnderscoreTemplate.fromString("<div class=\"coveo-quick-view-header\">\n    <div class=\"coveo-date\"><%- dateTime(raw.date) %></div>\n    <div class=\"coveo-email-from\">\n        From:\n        <span class=\"CoveoFieldValue\" data-field=\"@from\" data-helper=\"email\" data-html-value=\"true\"></span>\n    </div>\n\n    <div class=\"coveo-email-to\">\n        To:\n        <span class=\"CoveoFieldValue\" data-field=\"@recipients\" data-helper=\"email\" data-html-value=\"true\"></span>\n    </div>\n</div>\n<div class=\"CoveoEmailActions\"></div>\n<div class=\"CoveoQuickviewDocument\"></div>"), false, false)
Coveo.TemplateCache.registerTemplate("EmailChildResult", Coveo.UnderscoreTemplate.fromString("<div class=\"coveo-date\"><%- dateTime(raw.date) %></div>\n\n<div class=\"coveo-email-header\">\n    <span class=\"coveo-email-from\">\n        <% if(!isMobileDevice()) { %>\n            <%= email(raw.from, undefined, undefined, undefined, true) %>\n            <span class=\"CoveoQuickview\" data-template-id=\"EmailQuickviewContent\"></span>\n        <% } else { %>\n            <span class=\"CoveoFieldValue\" data-field=\"@from\" data-helper=\"email\" data-html-value=\"true\"></span>\n        <% } %>\n    </span>\n</div>\n\n<div class=\"coveo-excerpt\">\n    <% if(hasHtmlVersion && isMobileDevice()) { %>\n    <a class=\"CoveoSalesforceResultLink\" data-open-quickview=\"true\"><%= highlight(firstSentences, firstSentencesHighlights) %></a>\n    <span class=\"CoveoQuickview\" data-template-id=\"EmailQuickviewContent\"></span>\n    <% } else { %>\n    <a class=\"CoveoSalesforceResultLink\"><%= highlight(firstSentences, firstSentencesHighlights) %></a>\n    <% } %>\n</div>\n\n<div class=\"CoveoResultAttachments\" data-result-template-id=\"EmailResultAttachment\"></div>"), false, false)