Coveo.TemplateCache.registerTemplate("LithiumThread", Coveo.HtmlTemplate.fromString("<div class=\"coveo-result-frame\">\n  <div class=\"coveo-result-row\">\n    <div class=\"coveo-result-cell\" style=\"vertical-align: middle\">\n      <span class=\"CoveoIcon\" data-small=\"true\">\n      </span>\n      <a class=\"CoveoSalesforceResultLink\">\n      </a>\n    </div>\n    <div class=\"coveo-result-cell coveo-no-wrap\" style=\"text-align:right; padding-right:5px; width:126px;vertical-align: middle;font-size:12px;\">\n      <span class=\"CoveoAttachToCase\" data-display-text=\"false\"></span>\n      <span class=\"CoveoFieldValue\" data-field=\"@limessageposttime\" data-helper=\"dateTime\">\n      </span>\n    </div>\n    <div class=\"coveo-result-cell\" style=\"width:20px; vertical-align:middle; text-align:right\">\n      <div class=\"CoveoBoxResultAction\">\n        <div class=\"CoveoBoxQuickview\"></div>\n        <div class=\"CoveoBoxAttachToCase\"></div>\n      </div>\n    </div>\n  </div>\n  <div class=\"coveo-result-row\">\n    <div class=\"coveo-result-cell coveo-no-wrap\" style=\"white-space:nowrap\">\n      <span class=\"CoveoExcerpt\"></span>\n    </div>\n  </div>\n  <div class=\"coveo-result-row\">\n    <div class=\"coveo-result-cell\">\n      <span class=\"CoveoResultFolding\" data-result-template-id=\"LithiumThread\" data-more-caption=\"ShowCompleteThread\" data-less-caption=\"ShowOnlyTopMatchingPosts\"></span>\n    </div>\n  </div>\n</div>\n", "raw.connectortype == 'LithiumCrawler'"), true, true)
Coveo.TemplateCache.registerTemplate("LithiumChildResult", Coveo.UnderscoreTemplate.fromString("<div class=\"coveo-date\"><%- dateTime(raw.limessageposttime) %></div>\n\n<div class=\"coveo-lithium-header\">\n    <span class=\"coveo-lithium-from\">\n        From: <span class=\"CoveoFieldValue\" data-field=\"@limessageauthor\"></span>\n    </span>\n</div>\n\n<div class='coveo-excerpt coveo-show-if-normal'>\n    <a class=\"CoveoSalesforceResultLink\">\n        <%= highlight(firstSentences, firstSentencesHighlights) %>\n    </a>\n</div>\n\n<div class='coveo-excerpt coveo-show-if-expanded'>\n    <a class=\"CoveoSalesforceResultLink\">\n        <%= raw.limessagebody %>\n    </a>\n</div>"), false, false)